import React, { useEffect, useState } from "react";
import {
  Text,
  SafeAreaView,
  TextInput,
  Button,
  View,
  Image,
  TouchableOpacity,
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import styles from "./styles/styles";
import PropTypes from "prop-types";
import picture from "../assets/picture.jpg";

export default function ProfileScreen({ navigation }) {
  const [email, setEmail] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");

  // Load user data from AsyncStorage when the component mounts
  useEffect(() => {
    const loadUserData = async () => {
      const storedEmail = await AsyncStorage.getItem("email");
      const storedFirstName = await AsyncStorage.getItem("firstName");
      const storedLastName = await AsyncStorage.getItem("lastName");

      if (storedEmail) setEmail(storedEmail);
      if (storedFirstName) setFirstName(storedFirstName);
      if (storedLastName) setLastName(storedLastName);
    };

    loadUserData();
  }, []);

  // Handle saving updated user information
  const handleSave = async () => {
    await AsyncStorage.setItem("firstName", firstName);
    await AsyncStorage.setItem("lastName", lastName);
    alert("Changes saved successfully!"); // Optional: Provide feedback to user
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.profileContainer}>
        <Text style={styles.headerText}>Profile</Text>

        <TouchableOpacity onPress={() => console.log("Image pressed!")}>
          <Image source={picture} style={styles.profileImage} />
        </TouchableOpacity>

        <Text>Email/Username: {email}</Text>

        {/* Input fields for first name and last name */}
        <TextInput
          style={styles.input}
          placeholder="First Name"
          value={firstName}
          onChangeText={setFirstName}
        />
        <TextInput
          style={styles.input}
          placeholder="Last Name"
          value={lastName}
          onChangeText={setLastName}
        />

        <View style={styles.buttonContainer}>
          <Button title="Save Changes" onPress={handleSave} />
        </View>

        <View style={styles.buttonContainer}>
          <Button title="Back" onPress={() => navigation.goBack()} />
        </View>
      </View>
    </SafeAreaView>
  );
}

ProfileScreen.propTypes = {
  navigation: PropTypes.shape({
    navigate: PropTypes.func.isRequired,
    goBack: PropTypes.func.isRequired,
  }).isRequired,
};
