import React, { useState } from "react";
import {
  Text,
  SafeAreaView,
  TextInput,
  Button,
  View,
  StyleSheet,
} from "react-native"; // Assuming styles are in this file
import AsyncStorage from "@react-native-async-storage/async-storage";
import PropTypes from "prop-types";

export default function LoginScreen({ navigation, setIsLoggedIn }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [isLogin, setIsLogin] = useState(true);
  const [message, setMessage] = useState("");

  const handlePress = async () => {
    // Input validation
    if (isLogin) {
      if (email.trim() === "" || password.trim() === "") {
        setMessage("Please fill out all fields.");
        return;
      }
    } else {
      if (
        email.trim() === "" ||
        password.trim() === "" ||
        firstName.trim() === "" ||
        lastName.trim() === ""
      ) {
        setMessage("Please fill out all fields.");
        return;
      }
    }

    const url = isLogin
      ? "http://192.168.1.5:5000/users/login"
      : "http://192.168.1.5:5000/users/register";
    const body = isLogin
      ? { username: email, password }
      : { username: email, password, firstname: firstName, lastname: lastName };

    try {
      const response = await fetch(url, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(body),
      });

      const result = await response.json();
      if (response.ok) {
        setMessage("");
        if (isLogin) {
          await AsyncStorage.setItem("token", result.token);
          await AsyncStorage.setItem("email", email);
          await AsyncStorage.setItem(
            "firstName",
            result.firstname || firstName
          );
          await AsyncStorage.setItem("lastName", result.lastname || lastName);
          setIsLoggedIn(true);
          navigation.reset({
            index: 0,
            routes: [{ name: "Profile" }],
          });
        } else {
          setMessage("Registration successful. You can log in now.");
          setIsLogin(true); // Switch to login mode
        }
      } else {
        setMessage(result.message || "Something went wrong.");
      }
    } catch (error) {
      console.error("Error:", error);
      setMessage("Error connecting to the server.");
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View>
        <Text style={styles.headerText}>{isLogin ? "Login" : "Sign Up"}</Text>

        {!isLogin && (
          <>
            <TextInput
              style={styles.input}
              placeholder="Enter first name"
              value={firstName}
              onChangeText={(text) => setFirstName(text.trim())}
            />
            <TextInput
              style={styles.input}
              placeholder="Enter last name"
              value={lastName}
              onChangeText={(text) => setLastName(text.trim())}
            />
          </>
        )}

        <TextInput
          style={styles.input}
          placeholder="Enter email"
          value={email}
          onChangeText={(text) => setEmail(text.trim())}
          keyboardType="email-address"
        />
        <TextInput
          style={styles.input}
          placeholder="Enter password"
          value={password}
          onChangeText={(text) => setPassword(text)}
          secureTextEntry={true}
        />

        <View style={styles.buttonContainer}>
          <Button title={isLogin ? "Login" : "Sign Up"} onPress={handlePress} />
        </View>

        {message ? <Text style={styles.message}>{message}</Text> : null}

        <View style={styles.switchButtonContainer}>
          <Button
            title={isLogin ? "Switch to Sign Up" : "Switch to Login"}
            onPress={() => {
              setIsLogin(!isLogin);
              // Clear input fields and messages
              setEmail("");
              setPassword("");
              setFirstName("");
              setLastName("");
              setMessage("");
            }}
          />
        </View>
      </View>
    </SafeAreaView>
  );
}

LoginScreen.propTypes = {
  navigation: PropTypes.shape({
    navigate: PropTypes.func.isRequired,
    reset: PropTypes.func.isRequired,
  }).isRequired,
  setIsLoggedIn: PropTypes.func.isRequired,
};

// Example styles (customize according to your design)
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    padding: 20,
  },
  headerText: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
    textAlign: "center",
  },
  input: {
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginBottom: 15,
    paddingHorizontal: 10,
  },
  buttonContainer: {
    marginVertical: 10,
  },
  message: {
    color: "red",
    textAlign: "center",
    marginTop: 10,
  },
  switchButtonContainer: {
    marginTop: 20,
  },
});
